    gh_name = #{GhName}, gh_soc_num = #{GhSocNum}, gh_home_num = #{GhHomeNum}, gh_phone_num = #{GhPhoneNum}, gh_email = #{GhEmail},
    gh_job = #{GhJob}, gh_fax = #{GhFax}, gh_bank = #{GhBank}, gh_srttn = #{GhSrttn}, gh_year = #{GhYear}, gh_bsns_num = #{GhBsnsNum}, gh_ofc_num = #{GhOfcNum}, gh_ofc_name = #{GhOfcName},
    gh_acad_back = #{GhAcadBack}, gh_acad_back_file = #{GhAcadBackFile}, gh_carrer = #{GhCarrer}, gh_carrer_file = #{GhCarrerFile},
    gh_addr = #{GhAddr}, gh_dtl_addr = #{GhDtlAddr}
WHERE GH_NUM_SEQ = #{GhNum}
